mapper.update:
/* 특정 자격증 일정 수정*/
UPDATE 
	license_date
SET 
	 ld_period = #{ldPeriod}
	,ld_date = #{ldDate}
	,ld_acceptance_date = #{ldAcceptanceDate}
	,ld_cost = #{ldCost}
WHERE 
	ld_code= #{ldCode};

mapper.select:
/* 관리자 아이디 인증 */
SELECT
	if(COUNT(ld.ld_reg_id) > 0, 1, 0)
FROM
	license_date AS ld
WHERE
	ld.ld_reg_id = #{ldRegId};

/* 자격증 일정 목록 조회*/
SELECT
		*
FROM
	license_date AS ld;
