mapper.update:
/* 특정 자격증 수정*/
UPDATE 
	license_info
SET 
	 li_name = #{liName}
	,li_overview = #{liOverview}
	,li_qualification = #{liQualification}
WHERE 
	li_code= #{liCode};

mapper.select:
/* 관리자 아이디 인증 */
SELECT
	if(COUNT(li.li_reg_id) > 0, 1, 0)
FROM
	license_info AS li
WHERE
	li.li_reg_id = #{liRegId};


/* 자격증 정보 목록 조회*/
	SELECT
		*
	FROM
		license_info AS li;
