mapper.update:
/* 특정 포인트 적립 기준 수정*/
UPDATE 
		point_fee_rate
SET 
		point_fee_rate_contents = #{pointFeeRateContents},
		point_fee_rate = #{pointFeeRate}
WHERE 
		point_fee_rate_code= #{pointFeeRateCode};

mapper.select:
/* 관리자 아이디 인증 */
	SELECT
		if(COUNT(point_fee_rate_id) > 0, 1, 0)
	FROM
		point_fee_rate
	WHERE
		point_fee_rate_id = #{pointFeeRateId};

mapper.insert:
/* 포인트 수수료율 등록 */
INSERT INTO point_fee_rate
(
	point_fee_rate_code, 
	point_fee_rate_contents, 
	point_fee_rate, 
	point_fee_rate_id, 
	point_fee_rate_datetime
)VALUES(
	sf_point_fee_rate_code(), #{pointFeeRateContents}, #{pointFeeRate}, #{pointFeeRateId}, NOW()
);
