mapper.select:
/* 관리자 아이디 인증 */
SELECT
	if(COUNT(point_save_standard_id) > 0, 1, 0)
FROM
	point_save_standard
WHERE
	point_save_standard_id = #{pointSaveStandardId};

mapper.insert:
/* 포인트 적립 기준 등록 */
INSERT INTO point_save_standard
(
	point_save_standard_code, point_save_standard_content, save_point, point_save_standard_id
)VALUES(
	#{pointSaveStandardCode}, #{pointSaveStandardContent}, #{savePoint}, #{pointSaveStandardId}
);

mapper.select:
/* 포인트 적립 기준 내역 조회*/
SELECT
	*
FROM
	point_save_standard;
