mapper.update:
/* 특정 디데이 정보 수정*/
UPDATE 
	d_day
SET 
	 li_name = #{liName}
	,d_day_content = #{dDayContent}
	,d_day_date = #{dDayDate}
WHERE 
	d_day_code= #{dDayCode};

mapper.select:
/* 공부노트등록을 위한 자격증 목록 조회 */
SELECT
*
from
license_info;

			/* 디데이 상세 조회 */
			
		SELECT
			*
		FROM
			d_day ;

mapper.update:
/* 특정 일정 총공부시간 수정*/
UPDATE 
	to_do_list
SET 
	 total_day_study_time = #{stopwatchTime}
WHERE 
	to_do_list_code= #{toDoListCode};

mapper.delete:
/* 일정코드로 일정삭제 */
DELETE
FROM 
	to_do_list
WHERE 
	to_do_list_code = #{toDoListCode};

mapper.update:
/* 특정 일정 정보 수정*/
UPDATE 
	to_do_list
SET 
	 li_name = #{liName}
	,to_do_list_title = #{toDoListTitle}
	,to_do_list_day = #{toDoListDay}
WHERE 
	to_do_list_code= #{toDoListCode};

mapper.select:
			/* 일정 상세 조회 */
			
		SELECT
			*
		FROM
			to_do_list AS td;
