mapper.select:
			/* 디데이 상세 조회 */
			
		SELECT
			*
		FROM
			d_day AS dd;

mapper.delete:
/* 일정코드로 일정삭제 */
DELETE
FROM 
	to_do_list
WHERE 
	to_do_list_code = #{toDoListCode};

mapper.update:
/* 특정 일정 정보 수정*/
UPDATE 
	to_do_list
SET 
	 li_name = #{liName}
	,to_do_list_title = #{toDoListTitle}
WHERE 
	to_do_list_code= #{toDoListCode};

mapper.select:
			/* 일정 상세 조회 */
			
		SELECT
			*
		FROM
			to_do_list AS td;
